import React, { Component } from "react";
import Particles from "react-tsparticles";

class ParticleSettings extends Component {
  render() {
    return (
      <div>
        <Particles
          height="1000px"
          width="100vw"
          id="tsparticles"
          options={{ background: { color: { value: "#0d47a1" } } }}
        />
      </div>
    );
  }
}

export default ParticleSettings;
